'use strict';

var CalEvent = require('./CalEvent');

var Easter = require('./Easter'); // --- pre-processor instructions for prepin ---
// #ifndef nojulian


var Julian = require('./Julian'); // #endif
// #ifndef nohebrew


var Hebrew = require('./Hebrew'); // #endif
// #ifndef noislamic


var Hijri = require('./Hijri'); // #endif
// #ifndef noequinox


var Equinox = require('./Equinox'); // #endif
// #ifndef nochinese


var Chinese = require('./Chinese'); // #endif
// #ifndef nobengali


var BengaliRevised = require('./BengaliRevised'); // #endif


var CalEventFactory = function CalEventFactory(opts) {
  switch (opts.fn) {
    case 'easter':
      return new Easter(opts);
    // #ifndef nojulian

    case 'julian':
      return new Julian(opts);
    // #endif
    // #ifndef nohebrew

    case 'hebrew':
      return new Hebrew(opts);
    // #endif
    // #ifndef noislamic

    case 'islamic':
      return new Hijri(opts);
    // #endif
    // #ifndef noequinox

    case 'equinox':
      return new Equinox(opts);
    // #endif
    // #ifndef nochinese

    case 'chinese':
    case 'korean':
    case 'vietnamese':
      return new Chinese(opts);
    // #endif
    // #ifndef nobengali

    case 'bengali-revised':
      return new BengaliRevised(opts);
    // #endif

    default:
      return new CalEvent(opts);
  }
};

module.exports = CalEventFactory;